import jsPDF from 'jspdf'
import moment from 'moment'
import React, { Fragment, useState } from 'react'
import { Document } from 'react-pdf'
import { useHistory } from 'react-router-dom'
import '../fonts/SF-Pro-normal.js'
import '../fonts/SF-Pro-Text-Bold-bold.js'
import '../icons/user.png'
import store from '../store/store'

const Export2PdfPage = () => {
    const [previewPDF, setPreviewPdf] = useState('')    
    const storeState = store.getState().globalState
    const history = useHistory()
    
    const generatePdf = (type: string) => {
        const user = 'data:image/png;base64,'
        const address = 'data:image/png;base64,'
        const email = 'data:image/png;base64,'
        const phone = 'data:image/png;base64,'
        const work = 'data:image/png;base64,'
        const education = 'data:image/png;base64,'
        const skill = 'data:image/png;base64,'
        const doc:any = new jsPDF('p', 'mm', 'a4');
        doc.setFont('SF-Pro', "normal")
        doc.setLineWidth(0.4)

        doc.setDrawColor(0)
        doc.setFillColor(63, 101, 146)
        doc.rect(0, 0, 70, 300, 'F')
        let strings = storeState.profileImage.split(",");
        switch (strings[0]) {
            case "data:image/jpeg;base64":
                doc.addImage(storeState.profileImage, 'JPEG', 7.5, 5, 55, 55)       
            break;
            case "data:image/png;base64":
                doc.addImage(storeState.profileImage, 'PNG', 7.5, 5, 45, 45)  
            break;
            default:
                doc.addImage(storeState.profileImage, 'JPEG', 7.5, 5, 55, 55)  
            break;
        }
        doc.setTextColor(242)
        doc.setFontSize(14)
        let y = 70   

        doc.setFont('SF-Pro-Text-Bold', "bold")
        doc.text('CONTACT', 6.5, y)
        doc.setFontSize(12)
        y = y+5
        doc.addImage(user, 'PNG', 6.5, y-3, 3, 3)
        doc.text('Name', 12.5, y)
        y = y+5
        doc.setFont('SF-Pro', "normal")
        doc.text(`${storeState.firstName} ${storeState.lastName}`, 12.5, y)
        if (storeState.address !== ''){
            y = y + 7.5
            doc.addImage(address, 'PNG', 6.5, y-3, 3, 3)
            doc.setFont('SF-Pro-Text-Bold', "bold")
            doc.text('Address', 12.5, y)
            y = y + 5
            doc.setFont('SF-Pro', "normal")
            doc.text(storeState.address, 12.5, y)
            y = y + 5
            doc.text(`${storeState.zipCode} ${storeState.city}`, 12.5, y)
        }
        y = y + 7.5
        doc.addImage(email, 'PNG', 6.5, y-3, 3, 3)
        doc.setFont('SF-Pro-Text-Bold', "bold")
        doc.text('Email', 12.5, y)
        doc.setFont('SF-Pro', "normal")
        y = y + 5
        doc.text(storeState.email, 12.5, y)
        if (storeState.phoneNumber !== ''){
            y = y + 7.5
            doc.addImage(phone, 'PNG', 6.5, y-3, 3, 3)
            doc.setFont('SF-Pro-Text-Bold', "bold")
            doc.text('Phone number', 12.5, y)
            doc.setFont('SF-Pro', "normal")
            y = y +5
            doc.text(storeState.phoneNumber, 12.5, y)
        }
        if (storeState.hobbies.length !== 0) {
            y = y + 10
            doc.setFontSize(14)
            doc.setFont('SF-Pro-Text-Bold', "bold")
            doc.text('INTERESTS', 6.5, y)
            doc.setFont('SF-Pro', "normal")
            doc.setFontSize(12)
            y = y + 5
            for (let i = 0; i < storeState.hobbies.length; i++){
                doc.setFillColor(255)
                doc.rect(7, y-2.6, 2.5, 2.5, 'F')
                doc.text(storeState.hobbies[i].hobby, 12.5, y)
                y = y + 5
            }
        }

        //MAIN

        doc.setFontSize(18)
        doc.setTextColor(11)
        y = 10
        doc.setFont('SF-Pro-Text-Bold', "bold")
        doc.text(`${storeState.firstName.toUpperCase()} ${storeState.lastName.toUpperCase()}` , 80, y)
        doc.setFont('SF-Pro', "normal")
        doc.setTextColor(80)
        doc.setFontSize(10)
        if (storeState.resumeObjective !== ''){
            doc.setTextColor(160)
            y = y + 5
            let resumeObjective = doc.splitTextToSize(storeState.resumeObjective, 125)
            doc.text(resumeObjective, 80, y)
            let resumeObjectiveDimensions = doc.getTextDimensions(resumeObjective)
            let tempY = resumeObjectiveDimensions.h
            y = y + tempY
        }
        doc.setDrawColor(155)
        doc.line(80, y, 205, y)
        y = y + 10
        if (storeState.workExperiences.length !== 0){
            doc.setTextColor(11)
            doc.setFontSize(14)
            
            doc.setDrawColor(11)
            doc.circle(83.5, y-1.75, 5)
            doc.addImage(work, 'PNG', 80, y-5, 7, 7)
            doc.setFont('SF-Pro-Text-Bold', "bold")
            doc.text('WORK EXPERIENCE', 92.5, y) 
            doc.setFont('SF-Pro', "normal")
            y = y + 10
            
            for (let i = 0; i < storeState.workExperiences.length; i++){
                doc.setTextColor(11)
                doc.setFontSize(10)
                doc.setDrawColor(111)
                doc.circle(82.5, y-1.5, 1)
                
                if (!storeState.workExperiences[i].present)
                    doc.text(`${moment(storeState.workExperiences[i].startDate).format('MMM YYYY')} - ${moment(storeState.workExperiences[i].endDate).format('MMM YYYY')}`, 87.5, y)
                else
                    doc.text(`${moment(storeState.workExperiences[i].startDate).format('MMM YYYY')} - Present`, 87.5, y)
                    
                doc.setFont('SF-Pro-Text-Bold', "bold")
                doc.text(storeState.workExperiences[i].jobTitle, 127.5, y )
                doc.setFont('SF-Pro', "normal")
                y = y + 4
                doc.setTextColor(63, 101, 146)
                doc.text(`${storeState.workExperiences[i].employer}, ${storeState.workExperiences[i].city}`, 127.5, y)
                y = y + 4
                doc.setTextColor(160)
                doc.setFontSize(8)
                let description = doc.splitTextToSize(storeState.workExperiences[i].description, 79)
                doc.text(description, 127.5, y)
                let descriptionDimensions = doc.getTextDimensions(description)
                let tempY = descriptionDimensions.h
                y = y + tempY +5
                
            }
            doc.setDrawColor(155)
            doc.line(80, y-5, 205, y-5)
            y = y + 5
        }
        if (storeState.educations.length !== 0){
            doc.setTextColor(11)
            doc.setFontSize(14)
            doc.setDrawColor(11)
            doc.circle(83.5, y-1.75, 5)
            doc.addImage(education, 'PNG', 80, y-5, 7, 7)
            doc.setFont('SF-Pro-Text-Bold', "bold")
            doc.text('EDUCATION AND QUALIFICATIONS', 92.5, y)
            doc.setFont('SF-Pro', "normal")
            y = y + 10
            for (let i = 0; i < storeState.educations.length; i++){
                doc.setFontSize(10)
                doc.setTextColor(11)
                doc.setDrawColor(111)
                doc.circle(82.5, y-1.5, 1)

                if (!storeState.educations[i].present)
                    doc.text(`${moment(storeState.educations[i].startDate).format('MMM YYYY')} - ${moment(storeState.educations[i].endDate).format('MMM YYYY')}`, 87.5, y)
                else
                    doc.text(`${moment(storeState.educations[i].startDate).format('MMM YYYY')} - Present`, 87.5, y)
                
                doc.setFont('SF-Pro-Text-Bold', "bold")
                doc.text(storeState.educations[i].degree, 127.5, y )
                doc.setFont('SF-Pro', "normal")
                y = y + 4
                doc.setTextColor(63, 101, 146)
                let school = doc.splitTextToSize(`${storeState.educations[i].school}, ${storeState.educations[i].city} `, 79)
                doc.text(school, 127.5, y)
                let schollDimensions = doc.getTextDimensions(school)
                let tempY = schollDimensions.h
                y = y + tempY
                doc.setFontSize(8)
                doc.setTextColor(160)
                let description = doc.splitTextToSize(storeState.educations[i].description, 79)
                doc.text(description, 127.5, y)
                let descriptionDimensions = doc.getTextDimensions(school)
                tempY = descriptionDimensions.h
                y = y + tempY +5
                
            }
            doc.setDrawColor(155)
            doc.line(80, y-5, 205, y-5)
            y = y +5

        }

        if (storeState.skills.length !== 0){
            doc.setTextColor(11)
            doc.setFontSize(14)
            doc.setDrawColor(11)
            doc.circle(83.5, y-1.75, 5)
            doc.addImage(skill, 'PNG', 80, y-5, 7, 7)
            doc.setFont('SF-Pro-Text-Bold', "bold")
            doc.text('Skills', 92.5, y)
            let z = y + 5
            y = y + 10
            doc.setFontSize(11)
            for (let i = 0; i < storeState.skills.length; i++){
                doc.text(storeState.skills[i].skill, 87.5, y)
                doc.setDrawColor(0)
                let x = 150
                doc.setFillColor(63, 101, 146)
                switch(storeState.skills[i].level){
                    case ('Beginner'): {
                        doc.circle(x, y-1.5, 3, 'F')
                        x = x + 7.5
                        doc.setFillColor(178,194,212)
                        doc.circle(x, y-1.5, 3, 'F')
                        x = x + 7.5
                        doc.circle(x, y-1.5, 3, 'F')
                        x = x + 7.5
                        doc.circle(x, y-1.5, 3, 'F')
                        x = x + 7.5
                        doc.circle(x, y-1.5, 3, 'F')
                        break
                    }
                    case ('Intermediate'): {
                        doc.circle(x, y-1.5, 3, 'F')
                        x = x + 7.5
                        doc.circle(x, y-1.5, 3, 'F')
                        x = x + 7.5
                        doc.setFillColor(178,194,212)
                        doc.circle(x, y-1.5, 3, 'F')
                        x = x + 7.5
                        doc.circle(x, y-1.5, 3, 'F')
                        x = x + 7.5
                        doc.circle(x, y-1.5, 3, 'F')
                        break
                    }
                    case ('Skillfull'): {
                        
                        doc.circle(x, y-1.5, 3, 'F')
                        x = x + 7.5
                        doc.circle(x, y-1.5, 3, 'F')
                        x = x + 7.5
                        doc.circle(x, y-1.5, 3, 'F')
                        x = x + 7.5
                        doc.setFillColor(178,194,212)
                        doc.circle(x, y-1.5, 3, 'F')
                        x = x + 7.5
                        doc.circle(x, y-1.5, 3, 'F')
                        break
                    }
                    case ('Experienced'): {
                        doc.circle(x, y-1.5, 3, 'F')
                        x = x + 7.5
                        doc.circle(x, y-1.5, 3, 'F')
                        x = x + 7.5
                        doc.circle(x, y-1.5, 3, 'F')
                        x = x + 7.5
                        doc.circle(x, y-1.5, 3, 'F')
                        x = x + 7.5
                        doc.setFillColor(178,194,212)
                        doc.circle(x, y-1.5, 3, 'F')
                        break
                    }
                    case ('Expert'): {
                        doc.circle(x, y-1.5, 3, 'F')
                        x = x + 7.5
                        doc.circle(x, y-1.5, 3, 'F')
                        x = x + 7.5
                        doc.circle(x, y-1.5, 3, 'F')
                        x = x + 7.5
                        doc.circle(x, y-1.5, 3, 'F')
                        x = x + 7.5
                        doc.circle(x, y-1.5, 3, 'F')
                        break
                    }
                }
                y = y + 7.5
                
            }
            doc.setDrawColor(155)
            doc.line(83.5, z, 83.5, y - 5)
            doc.setFont('SF-Pro', "normal")

        }
        if (type === 'save')
            doc.save(`${storeState.firstName} ${storeState.lastName} Resume ${new Date().toISOString().slice(0,10)}` + '.pdf');
        else if (type === 'preview'){
            doc.output('pdfobjectnewwindow')

        }
    }
    
    return (

        <Fragment>
            <div id="preview">
                <h1 id="title">{storeState.firstName} {storeState.lastName}</h1>
                <button onClick={() => history.push('./advanced')}>Back</button>
                <button onClick={() => generatePdf('save')}>Downlaod pdf</button>
                <button onClick={() => generatePdf('preview')}>Preview pdf</button>
            </div>
        </Fragment>
    )
}
export default Export2PdfPage